import React from 'react';
import { useDispatch } from 'react-redux';
import { addFavoriteCity, removeFavoriteCity } from '../features/weatherSlice';

const CityList = ({ cities }) => {
  const dispatch = useDispatch();

  const toggleFavorite = (city) => {
    // Dodaj lub usuń miasto z ulubionych
    dispatch(addFavoriteCity(city));
  };

  return (
    <div>
      {cities.map(city => (
        <div key={city}>
          <h3>{city}</h3>
          <button onClick={() => toggleFavorite(city)}>
            Dodaj do ulubionych
          </button>
        </div>
      ))}
    </div>
  );
};

export default CityList;
