import React, { useEffect, useState } from 'react';
import axios from 'axios';

const WeatherDetails = ({ city }) => {
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async (city) => {
      console.log("City:", city); // Sprawdzamy, co jest przekazywane
    
      try {
        const response = await axios.get("https://api.openweathermap.org/data/2.5/weather", {
          params: {
            q: `${city},PL`, // Kod kraju dodany do zapytania
            appid: process.env.REACT_APP_WEATHER_API_KEY,
            units: "metric", // Jednostki metryczne
            lang: "pl", // Język polski
          },
        });
    
        console.log(response.data); // Zaloguj dane odpowiedzi
        setWeather(response.data); // Zaktualizuj stan aplikacji
      } catch (error) {
        if (error.response) {
          // Błąd zwrócony przez API
          console.error('API Error:', error.response.data);
          setError(`API Error: ${error.response.data.message}`);
        } else {
          // Inne błędy, np. problemy z połączeniem
          console.error('Unknown Error:', error.message);
          setError(`Error: ${error.message}`);
        }
      }
      
    };

    fetchWeather();
  }, [city]);

  if (error) {
    return <p>Błąd: {error}</p>;
  }

  if (!weather) {
    return <p>Ładowanie danych pogodowych...</p>;
  }

  return (
    <div>
      <h1>Pogoda w {weather.name}</h1>
      <p>Temperatura: {weather.main.temp}°C</p>
      <p>Warunki: {weather.weather[0].description}</p>
      <p>Wiatr: {weather.wind.speed} m/s, kierunek {weather.wind.deg}°</p>
    </div>
  );
};

export default WeatherDetails;
