import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const WeatherDetails = () => {
  const { city } = useParams(); // Odczytujemy nazwę miasta z URL
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        // Pobieranie bieżącej pogody
        const weatherResponse = await axios.get("https://api.openweathermap.org/data/2.5/weather", {
          params: {
            q: `${city},PL`, // Miasto z parametru URL
            appid: process.env.REACT_APP_WEATHER_API_KEY,
            units: "metric", // Jednostki metryczne
            lang: "pl", // Język polski
          },
        });

        setWeather(weatherResponse.data); // Ustawienie bieżącej pogody

        // Pobieranie prognozy na najbliższe 5 dni
        const forecastResponse = await axios.get("https://api.openweathermap.org/data/2.5/forecast", {
          params: {
            q: `${city},PL`,
            appid: process.env.REACT_APP_WEATHER_API_KEY,
            units: "metric",
            lang: "pl",
          },
        });

        setForecast(forecastResponse.data); // Ustawienie prognozy na 5 dni
      } catch (error) {
        setError("Błąd pobierania danych pogodowych.");
      }
    };

    fetchWeather();
  }, [city]);

  if (error) {
    return <p>{error}</p>;
  }

  if (!weather || !forecast) {
    return <p>Ładowanie danych...</p>;
  }

  const weatherIcon = `http://openweathermap.org/img/wn/${weather.weather[0].icon}.png`;

  return (
    <div>
      <h1>Pogoda w {weather.name}</h1>
      <p>Temperatura: {weather.main.temp}°C</p>
      <p>Warunki: {weather.weather[0].description}</p>
      <img src={weatherIcon} alt={weather.weather[0].description} />

      <p>Wiatr: {weather.wind.speed} m/s, kierunek {weather.wind.deg}°</p>
      <p>Stopień zachmurzenia: {weather.clouds.all}%</p>

      <h2>Prognoza na najbliższe 5 dni</h2>
      <div>
        {forecast.list.slice(0, 5).map((forecastItem, index) => (
          <div key={index}>
            <h3>{new Date(forecastItem.dt * 1000).toLocaleString()}</h3>
            <p>Temperatura: {forecastItem.main.temp}°C</p>
            <p>Warunki: {forecastItem.weather[0].description}</p>
            <img
              src={`http://openweathermap.org/img/wn/${forecastItem.weather[0].icon}.png`}
              alt={forecastItem.weather[0].description}
            />
            <p>Przewidywana ilość opadów: {forecastItem.rain ? forecastItem.rain["3h"] : 0} mm</p>
            <p>Prawdopodobieństwo opadów: {forecastItem.pop * 100}%</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WeatherDetails;
