// src/components/WeatherMap.js
import React, { useEffect } from 'react';
import L from 'leaflet';
import './WeatherMap.css';

const WeatherMap = ({ lat, lon }) => {
  useEffect(() => {
    const map = L.map('weather-map').setView([lat, lon], 8);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors'
    }).addTo(map);

    L.tileLayer(`https://tile.openweathermap.org/map/clouds_new/{z}/{x}/{y}.png?appid=${process.env.REACT_APP_WEATHER_API_KEY}`, {
      attribution: '© OpenWeatherMap',
      opacity: 1
    }).addTo(map);

    return () => {
      map.remove();
    };
  }, [lat, lon]);

  return <div id="weather-map" className="weather-map" />;
};

export default WeatherMap;
