import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './WeatherDetails.css'; // Import the CSS file

const WeatherDetails = () => {
  const { city } = useParams();
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const weatherResponse = await axios.get("https://api.openweathermap.org/data/2.5/weather", {
          params: {
            q: `${city},PL`,
            appid: process.env.REACT_APP_WEATHER_API_KEY,
            units: "metric",
            lang: "pl",
          },
        });

        setWeather(weatherResponse.data);

        const forecastResponse = await axios.get("https://api.openweathermap.org/data/2.5/forecast", {
          params: {
            q: `${city},PL`,
            appid: process.env.REACT_APP_WEATHER_API_KEY,
            units: "metric",
            lang: "pl",
          },
        });

        setForecast(forecastResponse.data);
      } catch (error) {
        setError("Błąd pobierania danych pogodowych.");
      }
    };

    fetchWeather();
  }, [city]);

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  if (!weather || !forecast) {
    return <p className="loading-message">Ładowanie danych...</p>;
  }

  const getWeatherIcon = (iconCode) => {
    return `https://openweathermap.org/img/wn/${iconCode}@2x.png`;
  };

  // Function to format the time
  const formatTime = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleTimeString('pl-PL', { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="container">
      <h1>Pogoda w {weather.name}</h1>
      
      <div className="weather-section">
        <div className="weather-info">
          <img src={getWeatherIcon(weather.weather[0].icon)} alt={weather.weather[0].description} />
          <div>
            <p><strong>Temperatura:</strong> {weather.main.temp}°C</p>
            <p><strong>Warunki:</strong> {weather.weather[0].description}</p>
            <p><strong>Wiatr:</strong> {weather.wind.speed} m/s, kierunek {weather.wind.deg}°</p>
            <p><strong>Zachmurzenie:</strong> {weather.clouds.all}%</p>
          </div>
        </div>
      </div>

      <div className="forecast-section">
        <h2>Prognoza na 5 dni</h2>
        <ul>
          {forecast.list.slice(0, 30).map((item, index) => (
            <li key={index} className="forecast-item">
              <p><strong>Data:</strong> {new Date(item.dt * 1000).toLocaleDateString()}</p>
              <p><strong>Godzina:</strong> {formatTime(item.dt)}</p> {/* Display the time */}
              <img src={getWeatherIcon(item.weather[0].icon)} alt={item.weather[0].description} />
              <p><strong>Temperatura:</strong> {item.main.temp}°C</p>
              <p><strong>Warunki:</strong> {item.weather[0].description}</p>
              <p><strong>Opady:</strong> {item.pop * 100}%</p>
              <p><strong>Ilość opadów:</strong> {item.rain ? item.rain['3h'] : 0} mm</p>
              <p><strong>Wiatr:</strong> {item.wind.speed} m/s, kierunek {item.wind.deg}°</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default WeatherDetails;
