import { createStore } from 'redux';

// Definicja początkowego stanu
const initialState = {
  temperatureUnit: 'metric', // Celsjusz jako domyślna jednostka
};

// Reducer - funkcja zarządzająca stanem
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_UNIT':
      return {
        ...state,
        temperatureUnit: action.payload,
      };
    default:
      return state;
  }
};

// Tworzenie store
const store = createStore(rootReducer);

export default store;
