import React, { useState, useEffect } from 'react';
import './CityList.css'; // Możesz dostosować style

const CityList = ({ cities }) => {
  const [favoriteCities, setFavoriteCities] = useState(() => {
    // Pobieranie ulubionych miast z localStorage (jeśli są)
    const savedFavorites = localStorage.getItem('favoriteCities');
    return savedFavorites ? JSON.parse(savedFavorites) : [];
  });

  const toggleFavorite = (city) => {
    let updatedFavorites;
    if (favoriteCities.includes(city)) {
      // Usuń miasto z ulubionych
      updatedFavorites = favoriteCities.filter(favCity => favCity !== city);
    } else {
      // Dodaj miasto do ulubionych
      updatedFavorites = [...favoriteCities, city];
    }
    setFavoriteCities(updatedFavorites);
    localStorage.setItem('favoriteCities', JSON.stringify(updatedFavorites)); // Zapisz do localStorage
  };

  return (
    <div className="city-list">
      <h2>Wybierz miasto</h2>
      <ul>
        {cities.map((city, index) => (
          <li key={index} className="city-item">
            <span>{city}</span>
            <button
              className={`favorite-btn ${favoriteCities.includes(city) ? 'active' : ''}`}
              onClick={() => toggleFavorite(city)}
              aria-label={`Dodaj ${city} do ulubionych`}
            >
              <span role="img" aria-label="serduszko">❤️</span>
            </button>
          </li>
        ))}
      </ul>

      <h3>Ulubione miasta</h3>
      <ul>
        {favoriteCities.length === 0 ? (
          <li>Brak ulubionych miast</li>
        ) : (
          favoriteCities.map((city, index) => (
            <li key={index}>{city}</li>
          ))
        )}
      </ul>
    </div>
  );
};

export default CityList;
