import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const WeatherDetails = () => {
  const { city } = useParams(); // Odczytujemy nazwę miasta z URL
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        // Pobranie bieżącej pogody
        const weatherResponse = await axios.get("https://api.openweathermap.org/data/2.5/weather", {
          params: {
            q: `${city},PL`, // Miasto z parametru URL
            appid: process.env.REACT_APP_WEATHER_API_KEY,
            units: "metric", // Jednostki metryczne
            lang: "pl", // Język polski
          },
        });

        setWeather(weatherResponse.data); // Ustaw dane w stanie

        // Pobranie prognozy na 7 dni (można zmienić limit dni)
        const forecastResponse = await axios.get("https://api.openweathermap.org/data/2.5/onecall", {
          params: {
            lat: weatherResponse.data.coord.lat, // Szerokość geograficzna miasta
            lon: weatherResponse.data.coord.lon, // Długość geograficzna miasta
            appid: process.env.REACT_APP_WEATHER_API_KEY,
            units: "metric", // Jednostki metryczne
            lang: "pl", // Język polski
            exclude: "hourly,minutely", // Wykluczamy dane godzinowe i minutowe
          },
        });

        console.log(forecastResponse.data); // Sprawdzenie odpowiedzi z API w konsoli
        setForecast(forecastResponse.data.daily); // Ustaw prognozę dzienną
      } catch (error) {
        setError("Błąd pobierania danych pogodowych.");
      }
    };

    fetchWeather();
  }, [city]); // Przeładowujemy zapytanie, gdy zmieni się miasto

  if (error) {
    return <p>{error}</p>;
  }

  if (!weather || !forecast) {
    return <p>Ładowanie danych...</p>;
  }

  // Funkcja pomocnicza do pobierania odpowiedniej ikony
  const getWeatherIcon = (iconCode) => {
    return `https://openweathermap.org/img/wn/${iconCode}@2x.png`;
  };

  return (
    <div>
      <h1>Pogoda w {weather.name}</h1>
      
      {/* Bieżące warunki pogodowe */}
      <div>
        <img src={getWeatherIcon(weather.weather[0].icon)} alt={weather.weather[0].description} />
        <p>Temperatura: {weather.main.temp}°C</p>
        <p>Warunki: {weather.weather[0].description}</p>
        <p>Wiatr: {weather.wind.speed} m/s, kierunek {weather.wind.deg}°</p>
        <p>Stopień zachmurzenia: {weather.clouds.all}%</p>
      </div>

      {/* Prognoza na najbliższe 7 dni */}
      <div>
        <h2>Prognoza na 7 dni</h2>
        {Array.isArray(forecast) ? (
          <ul>
            {forecast.map((item, index) => (
              <li key={index}>
                <p>Data: {new Date(item.dt * 1000).toLocaleDateString()}</p>
                <img src={getWeatherIcon(item.weather[0].icon)} alt={item.weather[0].description} />
                <p>Temperatura: {item.temp.day}°C</p>
                <p>Warunki: {item.weather[0].description}</p>
                <p>Prawdopodobieństwo opadów: {item.pop * 100}%</p>
                <p>Ilość opadów: {item.rain ? item.rain : 0} mm</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>Brak prognozy na najbliższe dni.</p>
        )}
      </div>
    </div>
  );
};

export default WeatherDetails;
