import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import L from 'leaflet';
import './WeatherDetails.css';

let mapInstance = null; // Globalna zmienna do przechowywania instancji mapy

const WeatherDetailsPage = () => {
  const { city } = useParams();
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);
  const [temperatureUnit] = useState(() => {
    return localStorage.getItem('temperatureUnit') || 'metric';
  });

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const weatherResponse = await axios.get("https://api.openweathermap.org/data/2.5/weather", {
          params: {
            q: `${city},PL`,
            appid: process.env.REACT_APP_WEATHER_API_KEY,
            units: temperatureUnit,
            lang: "pl",
          },
        });
        setWeather(weatherResponse.data);
      } catch (error) {
        setError("Błąd pobierania danych pogodowych.");
      }
    };

    fetchWeather();
  }, [city, temperatureUnit]);

  useEffect(() => {
    if (weather) {
      // Sprawdź, czy mapa już istnieje
      if (mapInstance) {
        mapInstance.remove(); // Usuń istniejącą mapę, aby uniknąć błędu
      }

      // Inicjalizacja mapy
      mapInstance = L.map('map').setView([weather.coord.lat, weather.coord.lon], 10);

      // Dodanie warstwy OpenStreetMap
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
      }).addTo(mapInstance);

      // Dodanie warstwy radaru pogodowego z OpenWeatherMap
      L.tileLayer(`https://tile.openweathermap.org/map/precipitation_new/{z}/{x}/{y}.png?appid=${process.env.REACT_APP_WEATHER_API_KEY}`, {
        maxZoom: 19,
        opacity: 0.5,
      }).addTo(mapInstance);
    }
  }, [weather]);

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  if (!weather) {
    return <p className="loading-message">Ładowanie danych...</p>;
  }

  return (
    <div className="container">
      <h1>Pogoda w {weather.name}</h1>
      <div className="weather-section">
        <div className="weather-info">
          <img src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`} alt={weather.weather[0].description} />
          <div>
            <p><strong>Temperatura:</strong> {weather.main.temp}{temperatureUnit === 'metric' ? '°C' : '°F'}</p>
            <p><strong>Warunki:</strong> {weather.weather[0].description}</p>
            <p><strong>Wiatr:</strong> {weather.wind.speed} {temperatureUnit === 'metric' ? 'm/s' : 'mph'} ({weather.wind.deg}°)</p>
            <p><strong>Zachmurzenie:</strong> {weather.clouds.all}%</p>
            <p><strong>Opady:</strong> {weather.rain ? weather.rain["3h"] : 0} mm deszczu</p>
          </div>
        </div>
      </div>
      <div id="map" style={{ width: '400px', height: '400px', margin: '20px auto' }}></div>
    </div>
  );
};

export default WeatherDetailsPage;
