import React, { useEffect, useState } from 'react';
import axios from 'axios';

const WeatherDetails = ({ city }) => {
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const response = await axios.get('https://api.openweathermap.org/data/2.5/weather', {
          params: {
            q: city,
            appid: process.env.REACT_APP_WEATHER_API_KEY, // Użyj klucza z pliku .env
            units: 'metric', // Jednostki w Celsiuszach
            lang: 'pl', // Język polski
          },
        });

        console.log(response.data); // Dodaj logi, aby sprawdzić dane
        setWeather(response.data);
      } catch (error) {
        setError(error.message);
        console.error('Error fetching weather data:', error); // Loguj błędy
      }
    };

    fetchWeather();
  }, [city]);

  if (error) {
    return <p>Błąd: {error}</p>;
  }

  if (!weather) {
    return <p>Ładowanie danych pogodowych...</p>;
  }

  return (
    <div>
      <h1>Pogoda w {weather.name}</h1>
      <p>Temperatura: {weather.main.temp}°C</p>
      <p>Warunki: {weather.weather[0].description}</p>
      <p>Wiatr: {weather.wind.speed} m/s, kierunek {weather.wind.deg}°</p>
    </div>
  );
};

export default WeatherDetails;
