import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import CityList from './components/CityList';
import WeatherDetails from './components/WeatherDetails';
import './App.css'; // Importowanie globalnego pliku CSS

const cities = ['Warszawa', 'Kraków', 'Gdańsk', 'Wrocław', 'Poznań', 'Opole'];

const App = () => {
  // Ustawienie początkowego stanu na podstawie danych w localStorage
  const [temperatureUnit, setTemperatureUnit] = useState(localStorage.getItem('temperatureUnit') || 'Celsius');
  const [favoriteCities, setFavoriteCities] = useState(
    JSON.parse(localStorage.getItem('favoriteCities')) || []
  );

  // Funkcja do zmiany jednostki temperatury
  const changeTemperatureUnit = (unit) => {
    setTemperatureUnit(unit);
    localStorage.setItem('temperatureUnit', unit); // Zapisz do localStorage
  };

  // Funkcja do dodawania ulubionych miast
  const toggleFavoriteCity = (city) => {
    const updatedFavorites = favoriteCities.includes(city)
      ? favoriteCities.filter((item) => item !== city)
      : [...favoriteCities, city];
    setFavoriteCities(updatedFavorites);
    localStorage.setItem('favoriteCities', JSON.stringify(updatedFavorites)); // Zapisz do localStorage
  };

  useEffect(() => {
    // Upewnij się, że dane w localStorage są aktualizowane na początku
    localStorage.setItem('favoriteCities', JSON.stringify(favoriteCities));
  }, [favoriteCities]);

  return (
    <Router>
      <div className="app-container">
        <header className="app-header">
          <h1>Prognoza Pogody</h1>
          <nav>
            <ul className="nav-list">
              <li><a href="/">Strona Główna</a></li>
            </ul>
            <div>
              <label>Jednostka temperatury:</label>
              <select
                value={temperatureUnit}
                onChange={(e) => changeTemperatureUnit(e.target.value)}
              >
                <option value="Celsius">Celsius</option>
                <option value="Fahrenheit">Fahrenheit</option>
              </select>
            </div>
          </nav>
        </header>
        <Routes>
          <Route 
            path="/" 
            element={<CityList cities={cities} toggleFavoriteCity={toggleFavoriteCity} favoriteCities={favoriteCities} />} 
          />
          <Route 
            path="/weather/:city" 
            element={<WeatherDetails temperatureUnit={temperatureUnit} />} 
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
