import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import CityList from './components/CityList';
import WeatherDetails from './components/WeatherDetails';

const App = () => {
  const [temperatureUnit, setTemperatureUnit] = useState(() => {
    // Pobierz jednostki temperatury z localStorage
    const savedUnit = localStorage.getItem('temperatureUnit');
    return savedUnit ? savedUnit : 'metric'; // domyślnie 'metric' (Celsius)
  });

  // Funkcja zmiany jednostek temperatury
  const toggleTemperatureUnit = () => {
    const newUnit = temperatureUnit === 'metric' ? 'imperial' : 'metric';
    setTemperatureUnit(newUnit);
    localStorage.setItem('temperatureUnit', newUnit); // Zapisz w localStorage
  };

  return (
    <Router>
      <div>
        <button onClick={toggleTemperatureUnit}>
          Zmień jednostki temperatury ({temperatureUnit === 'metric' ? '°C' : '°F'})
        </button>
        <Routes>
          <Route path="/" element={<CityList />} />
          <Route path="/weather/:city" element={<WeatherDetails temperatureUnit={temperatureUnit} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
